package CastingProgram;

public class Example3 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		 double x=200.52d;
		 int y=(int)x;   // manual casting : double to int: explicit narrowing
		 
		 System.out.println(x);// out put = 200.52
		 System.out.println(y);// output= 200 
		 
		 // in explicit narrowing canverting higher data type in to lower data type chance to  lose data 
			
		 
		 

	}

}
