package Inheritance;

public class WhileLoop {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int i=0;
		while(i<10) {
			System.out.println("i:"+i);
			i++;
		}
		char c1='A';
		while(c1<='Z') {
			System.out.println(c1);
			c1++;
			
		}
		char c2='z';
		while(c2>='a') {
			System.out.println(c2);
			c2--;
			
		}
		int r=100;
		while(r>=1) {
			System.out.println(r);
			r--;
		}
		

	}

}
